      const response = await fetch('https://fvvpfueoaacijowkpdsf.supabase.co/rest/v1/users', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'apikey': 'eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImZ2dnBmdWVvYWFjaWpvd2twZHNmIiwicm9sZSI6ImFub24iLCJpYXQiOjE3NjM0MzYwODIsImV4cCI6MjA3OTAxMjA4Mn0.4PMgaMn33jJ36tN7UISTBbsCKTczhAlquQTkqAq7ApI',
          'Authorization': 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpc3MiOiJzdXBhYmFzZSIsInJlZiI6ImZ2dnBmdWVvYWFjaWpvd2twZHNmIiwicm9sZSI6ImFub24iLCJpYXQiOjE3NjM0MzYwODIsImV4cCI6MjA3OTAxMjA4Mn0.4PMgaMn33jJ36tN7UISTBbsCKTczhAlquQTkqAq7ApI',
          'Prefer': 'return=minimal'
        },
        body: JSON.stringify({ 
          email: formData.email,
          name: formData.name
        })
      });

      if (step < 2) {
        setStep(step + 1);
      } else {
        toast.success("Account created! Redirecting to dashboard...");
        setTimeout(() => navigate('/dashboard'), 1500);
      }
    } catch (error) {
      toast.error("Something went wrong. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-muted/30 p-4">
